options.customCss:
:root {
	--font-family: 'Roboto', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 700;
	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-input: 14px;
	--font-size-header: 26px; /* Bigger title */
	--font-size-paragraph: 14px;
	--font-size-link: 12px;

	/* Colors - Dark Mode */
	--color-background: #121212;
	--color-card-bg: #1e1e1e;
	--color-card-border: #333;
	--color-card-shadow: rgba(0, 0, 0, 0.6);
	--color-header: #ffffff;
	--color-label: #cccccc;
	--color-input-border: #333;
	--color-input-text: #000000; /* black text when typing */
	--color-input-bg: #ffffff; /* white input background */
	--color-focus-border: #ff9800;
	--color-submit-btn-bg: #ff9800;
	--color-submit-btn-text: #ffffff;
	--color-error: #ff5252;
	--color-html-text: #e0e0e0;
	--color-html-link: #ff9800;

	/* Border Radii */
	--border-radius-card: 8px;
	--border-radius-input: 6px;

	/* Spacing */
	--padding-container-top: 24px;
	--padding-card: 24px;
	--margin-bottom-card: 16px;
	--padding-form-input: 12px;

	/* Dimensions */
	--container-width: 448px;
	--submit-btn-height: 48px;

	/* Others */
	--box-shadow-card: 0px 4px 16px 0px var(--color-card-shadow);
}

/* General Layout */
body {
	background-color: var(--color-background);
	color: var(--color-html-text);
	font-family: var(--font-family);
	font-weight: var(--font-weight-normal);
}

/* Title */
h1, .form-title {
	font-size: var(--font-size-header);
	font-weight: var(--font-weight-bold);
	color: var(--color-header);
	margin-bottom: 20px;
}

/* Labels */
label {
	color: var(--color-label);
	font-size: var(--font-size-label);
	display: block;
	margin-bottom: 6px;
}

/* Inputs */
input, textarea, select {
	width: 100%;
	padding: var(--padding-form-input);
	border: 1px solid var(--color-input-border);
	border-radius: var(--border-radius-input);
	background-color: var(--color-input-bg);
	color: var(--color-input-text);
	font-size: var(--font-size-input);
	box-sizing: border-box;
}

input:focus, textarea:focus, select:focus {
	outline: none;
	border-color: var(--color-focus-border);
	box-shadow: 0 0 4px var(--color-focus-border);
}

input::placeholder {
	color: #888;
}

/* Buttons */
button, .submit-btn {
	background-color: var(--color-submit-btn-bg);
	color: var(--color-submit-btn-text);
	border: none;
	border-radius: var(--border-radius-input);
	height: var(--submit-btn-height);
	width: 100%;
	font-weight: var(--font-weight-bold);
	font-size: 14px;
	cursor: pointer;
	transition: background-color 0.2s ease;
}

button:hover, .submit-btn:hover {
	background-color: #ffb74d;
}

/* Card / Container */
.card {
	background-color: var(--color-card-bg);
	border: 1px solid var(--color-card-border);
	border-radius: var(--border-radius-card);
	padding: var(--padding-card);
	box-shadow: var(--box-shadow-card);
	width: var(--container-width);
	margin: auto;
}
